function setup() {
  colorPicker1 = createColorPicker('#7F7F7F');
  colorPicker1.position(0,0);
  
  colorPicker2 = createColorPicker('#FFFFFF');
  colorPicker2.position(50,0);
  
  colorPicker3 = createColorPicker('#000000');
  colorPicker3.position(100,0);
  
  colorPicker4 = createColorPicker('#0000FF');
  colorPicker4.position(150,0);
  
  colorPicker5 = createColorPicker('#FFA700');
  colorPicker5.position(200,0);
  
  createCanvas(700, 700);

}

function draw() {
  g=0
  for (let j = 0; j < 35; j++){
    for (let i = 0; i < 35; i++){
    if(Amap[j][i]==" "){
    Pix[g] = new pix(i*20,j*20,colorPicker1.color())
    }
    if(Amap[j][i]=="W"){
    Pix[g] = new pix(i*20,j*20,colorPicker2.color())
    }
    if(Amap[j][i]=="B"){
    Pix[g] = new pix(i*20,j*20,colorPicker3.color())
    }
    if(Amap[j][i]=="b"){
    Pix[g] = new pix(i*20,j*20,colorPicker4.color())
    }
    if(Amap[j][i]=="O"){
    Pix[g] = new pix(i*20,j*20,colorPicker5.color())
    }
    Pix[g].show();
    g++
  }
}
  console.log(colorPicker4.color())
}
class pix {
  constructor(_x,_y,_r){
    this.x=_x
    this.y=_y 
    this.r=_r 
  }
  show(){
    noStroke()
    fill(this.r)
    rect(this.x,this.y,20)
  }
  
}